"use strict";define(["ui","../apis/app"],function(require,exports,module){var UI=require("ui"),App=require("../apis/app"),appId=$("#data_app_id").val();$(".secret-component .toggle-btn").on("click",function(e){e.preventDefault(),$(".secret-component").toggleClass("open")}),$("#btn_refresh_secret").on("click",function(e){App.refreshSecret(appId).then(function(res){res&&res.id?$("#app_secrect_field").html(res.secret):UI.alert("应用secret刷新失败，请稍后再试~")}).catch(function(err){err&&err.message?UI.alert(err.message):UI.alert("应用secret刷新失败，请稍后再试~")})}),$("#btn_app_confirm").on("click",function(e){App.confirm(appId).then(function(res){res&&res.id?UI.alert("应用已审核通过~").then(function(){location.reload()}):UI.alert("操作失败，请稍后再试~")}).catch(function(err){err&&err.message?UI.alert(err.message):UI.alert("操作失败，请稍后再试~")})}),$("#btn_app_reject").on("click",function(e){App.reject(appId).then(function(res){res&&res.id?UI.alert("操作成功，应用审核不通过~").then(function(){location.reload()}):UI.alert("操作失败，请稍后再试~")}).catch(function(err){err&&err.message?UI.alert(err.message):UI.alert("操作失败，请稍后再试~")})})});